vec4 Effect2(vec2 uv) {
    // Get mouse position from texture (assumes iChannel0 stores mouse data)
    vec2 mousePos = texture(iChannel0, vec2(0.5, 0.5)).xy;

    // Time-based rotation factor
    float timeFactor = iTime * PI * 2.0;

    // Transform UV coordinates with rotation and scaling
    mat2 rotMatrix = mat2(cos(timeFactor), sin(timeFactor),
                          -sin(timeFactor), cos(timeFactor));
    vec2 transformedUV = rotMatrix * (uv * 5.0) + mousePos * 0.1;

    // Generate multi-octave noise with mouse influence
    float noiseValue = turbulent(transformedUV);

    // Convert noise to HSL and then to RGB for color variation
    vec3 hslColor = hsl(noiseValue * 0.5 + 0.5, 
                        sin(timeFactor) * 0.5 + 0.5,
                        cos(timeFactor) * 0.5 + 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine with original UV for final output
    return vec4(rgbColor, 1.0) * noiseValue;
}
