vec4 Effect2(vec2 uv) {
    vec2 st = uv * 5.0;
    float time = iTime * 0.5;

    // Create rotating pattern
    float angle = atan2(st.y - 0.5, st.x - 0.5);
    angle += time;

    // Add noise and turbulence
    float n1 = noise(vec3(st + time, 0.0)) * 0.5 + 0.5;
    float t1 = turbulent(st * 2.0) * 0.5 + 0.5;

    // Combine elements for color
    vec3 color = hsl2rgb(vec3(
        (angle + time) / (2.0 * PI),
        n1,
        t1
    ));

    return vec4(color, 1.0);
}
