vec4 Effect2(vec2 uv) {
    vec3 pos = vec3(uv * 5., iTime * 0.1);
    float t = turbulent(pos);

    vec2 noiseUV = (uv + noise(uv * 0.5)) * 0.5;
    float n = noise(noiseUV * 4.) * 0.5 + 0.5;

    vec3 color = hsv2rgb(vec3(
        t * 0.5 + 0.5, 
        0.8 + sin(iTime) * 0.2,
        0.6 + n * 0.4
    ));

    float d = length(uv - 0.5);
    float a = smoothstep(0.3, 1., abs(sin(d * 5. + iTime)));

    return vec4(color * (0.2 + a), a);
}
