vec4 Effect2(vec2 uv) {
    vec3 color = hsv2rgb(vec3(
        iTime * 0.1 + sin(iTime) * 0.5,
        0.7 + noise(uv * 0.5 + iTime) * 0.3,
        0.8 + turbulent(uv * 4.0 - iTime * 2.0)
    ));

    vec2 pos = uv;
    float timeScale = iTime * 0.1;

    // Add flowing displacement
    vec2 flow = noise(pos * 0.5 + iTime) * vec2(sin(timeScale), cos(timeScale)) * 0.3;
    pos += flow;

    // Create molten effect with glow
    float distanceFromEdge = length(vec2(1.0) - abs(pos));
    color *= smoothstep(0.0, 0.5, distanceFromEdge);

    // Add glowing edges
    vec3 glow = vec3(1.0, 0.8, 0.4) * exp(-distanceFromEdge * 2.0);
    color += glow;

    return vec4(color, 1.0);
}
