vec4 Effect2(vec2 uv) {
    vec2 timeMove = sin(iTime * 0.5) * vec2(3., 1.);
    vec2 lightPos = vec2(sin(iTime), cos(iTime)) * 0.5 + timeMove;

    vec2 dist = uv - lightPos;
    float d = length(dist);

    float f = turbulent(vec2(uv.x * 10. + iTime, uv.y * 10. + iTime));
    f *= sin(d * 3.) * 0.5 + 0.5;

    vec3 color = hsl2rgb(vec3(f * 0.5 + 0.5, 1., 0.8));

    return vec4(color, 1.);
}
