vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create shifting patterns using sine functions
    vec3 color = vec3(
        sin(time + uv.x * 10.0) * 0.5 + 0.5,
        sin(time * 2.0 + uv.y * 5.0) * 0.5 + 0.5,
        sin(time * 0.7 + uv.x * 3.0 + uv.y * 4.0) * 0.5 + 0.5
    );

    // Add noise and turbulence for more dynamic effect
    vec2 noiseUV = uv * 10.0;
    float noiseValue = noise(noiseUV);
    color += turbulent(vec3(uv, time)) * 0.3;

    // Create flowing color transitions
    color.r = sin(color.r * pi + time) * 0.5 + 0.5;
    color.g = sin(color.g * pi + time * 0.7) * 0.5 + 0.5;
    color.b = sin(color.b * pi + time * 1.3) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
