vec4 Effect2(vec2 uv) {
    // Create pulsating nebula effect with vibrant colors and radial motion

    // Calculate distance from center
    float d = length(uv);

    // Base noise pattern
    vec2 pos = uv * 10.0;
    float n = noise(pos + iTime) * 0.5;

    // Add turbulence at different scales
    float t1 = turbulent(pos * 3.0 + iTime * 2.0);
    float t2 = turbulent(pos * 6.0 + iTime * 4.0);

    // Combine noise and turbulence
    float c = (n + t1 * 0.5 + t2 * 0.2) * 0.5;

    // Add pulsation effect
    c += sin(iTime * 3.0 + d * 4.0) * 0.3;

    // Convert to RGB with vibrant colors
    vec3 col = hsl2rgb(vec3(
        (length(uv * 5.0 + iTime)) * 0.4,
        0.8,
        c * 0.6 + 0.4
    ));

    // Create radial gradient effect
    vec3 finalColor = mix(col, vec3(0.0), smoothstep(1.5, 0.0, d));

    return vec4(finalColor, 1.0);
}
