vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv * 1.5); // Scale radius for better spiral shape

    // Add time-based rotation
    theta += iTime * 0.1;

    // Create spiral arms pattern using sine function
    float spiral = sin(theta * 6.0 + iTime) * exp(-r * r / 4.0);
    spiral = pow(abs(spiral), 2.0); // Sharpen the spiral arms

    // Add turbulence for dynamic effect
    vec3 noisePos = vec3(uv.x, uv.y, iTime * 0.1);
    float turb = turbulent(noisePos * 5.0) * 0.5 + 0.5;

    // Create star particles using high-frequency noise
    float stars = noise(uv * 100.0 + iTime) * 0.3;
    stars = smoothstep(0.8, 1.0, stars);

    // Combine spiral arms and turbulence
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.5 + r * 2.0) * 0.5 + 0.5, // Hue variation
        0.7 + turb * 0.3, // Saturation
        0.1 + spiral * 0.4 + stars * 0.6 // Lightness
    ));

    return vec4(color, 1.0);
}
