vec4 Effect2(vec2 uv) {
    // Transform UV coordinates to range (-1,1)
    vec2 st = uv * 2.0 - 1.0;

    // Create multiple sine wave layers with varying frequencies and amplitudes
    float wave1 = sin(st.x * 6.0 + iTime * 2.0) * 0.5;
    float wave2 = sin(st.y * 4.0 + iTime * 3.0) * 0.7;
    float wave3 = sin((st.x + st.y) * 8.0 + iTime) * 0.6;
    float wave4 = sin((st.x - st.y) * 12.0 + iTime * 5.0) * 0.4;

    // Combine waves with some offset and modulation
    float combinedWaves = (wave1 + wave2 + wave3 + wave4) * 0.5;

    // Add turbulence for dynamic interference pattern
    vec2 noiseCoords = st * 0.5 + iTime * 0.2;
    float turbulence = turbulent(noiseCoords);

    // Create final pattern by combining waves and turbulence
    float pattern = (combinedWaves + turbulence) * 0.5 + 0.5;

    // Convert to color using HSL for vibrant results
    vec3 color = hsl2rgb(vec3(
        pattern * 360.0,   // Hue varies with pattern
        0.7 + pattern * 0.3,  // Saturation modulation
        0.5                   // Fixed lightness
    ));

    return vec4(color, 1.0);
}
