vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based rotation
    theta += sin(iTime * 2.0) * 3.0;
    // Create geometric pattern using sine functions
    float pattern = sin(theta + sin(r * 10.0 + iTime)) * cos(r * 5.0 - iTime);

    // Add turbulence for dynamic effect
    vec2 noiseUV = uv * 10.0 + iTime;
    float turbulentValue = turbulent(noiseUV);
    // Create color transitions using HSL
    vec3 hslColor = vec3(
        sin(iTime) * 0.5 + 0.5,   // Hue
        0.7,                      // Saturation
        0.5 + turbulentValue * 0.2 // Lightness
    );
    // Convert HSL to RGB and mix with pattern
    vec3 color = hsl2rgb(hslColor);
    color *= abs(pattern) * 0.5 + 0.5;
    return vec4(color, 1.0);
}
