vec4 Effect2(vec2 uv) {
    // Create square wave pattern based on UV coordinates and time
    vec2 st = uv * 8.0; // Scale up for multiple squares
    float sqx = sign(sin(st.x + iTime)) * 0.5 + 0.5;
    float sqy = sign(cos(st.y - iTime)) * 0.5 + 0.5;

    // Create moving square wave pattern
    vec2 pattern = vec2(sqx, sqy);

    // Invert colors based on pattern
    vec3 color = hsl2rgb(vec3(0.5, 1.0, pattern.x * pattern.y));
    color = (pattern.x + pattern.y > 1.0) ? vec3(1.0) - color : color;

    // Add turbulence for extra detail
    float turb = turbulent(vec3(uv * 4.0, iTime * 0.5)) * 0.2;
    color += turb;

    return vec4(color, 1.0);
}
