vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [0,1]
    vec2 st = uv * 0.5 + 0.5;

    // Create a radial pattern
    vec2 pos = st - 0.5;
    float d = length(pos);
    float angle = atan(pos.y, pos.x) + iTime * 0.5;

    // Create rotating color wheels using HSL colors
    vec3 wheelColor = hsl(angle * 0.16 + 0.5, 0.7, 0.6);

    // Add pulsating rings with noise and turbulence
    float ring = sin(d * 8.0 + iTime) * 0.5;
    float turb = turbulent(vec3(st * 4.0, iTime * 0.1)) * 0.2;
    float finalRadius = d + ring + turb;

    // Create color transitions
    vec3 color = mix(wheelColor,
                     hsl(finalRadius * 0.5 + 0.5, 0.8, 0.7),
                     smoothstep(0.4, 1.0, finalRadius));

    // Add some noise variation
    color += noise(vec3(st * 2.0, iTime)) * 0.1;

    return vec4(color, 1.0);
}
