vec4 Effect2(vec2 uv) {
    // Create hexagonal tiling pattern
    vec2 q = uv * 3.0; // Scale up for more hexagons
    q.x += turbulent(q + iTime); // Add dynamic movement

    // Convert to polar coordinates
    float r = length(q);
    float theta = atan2(q.y, q.x) / (pi * 2.0);

    // Create hexagonal grid pattern
    vec2 grid = floor(vec2(r * cos(theta * pi), r * sin(theta * pi)) + 0.5);

    // Calculate distance from center of hexagon
    vec2 center = fract(grid) - 0.5;
    float dist = length(center);

    // Create color based on distance and time
    vec3 hslColor = vec3(fract(iTime * 0.1 + grid.x * 0.1), 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add pulsing effect
    float pulse = sin(iTime) * 0.5 + 0.5;
    rgbColor *= mix(0.5, 1.0, pulse);

    return vec4(rgbColor, 1.0);
}
