vec4 Effect2(vec2 uv) {
    // Create a base color using HSL with time-varying hue
    vec3 hslColor = hsl(uv.x * 5.0 + iTime, 1.0, 0.5);

    // Add flowing movement using noise and turbulence
    vec2 pos = uv * 4.0;
    pos.x += iTime * 0.5;

    float flow = turbulent(vec2(pos.x, pos.y)) * 0.3;
    flow += turbulent(vec2(pos.x * 0.5, pos.y * 0.5) + vec2(123.456, 789.123)) * 0.2;

    // Create metallic effect with highlights and shadows
    float highlight = noise(vec2(uv.x * 10.0 + iTime * 0.2, uv.y * 10.0)) * 0.5 + 0.5;
    float shadow = noise(vec2(uv.x * 20.0 - iTime * 0.3, uv.y * 20.0)) * 0.5 + 0.5;

    // Combine all elements
    vec3 color = hslColor * (flow + 0.7);
    color *= mix(1.0, 0.3, shadow);
    color += highlight * 0.4;

    return vec4(color, 1.0);
}
