vec4 Effect2(vec2 uv) {
    // Create a base noise value with time offset
    float baseNoise = noise(uv * 5.0 + iTime);

    // Add some turbulence for more dynamic patterns
    vec3 pos3D = vec3(uv * 10.0, iTime * 2.0);
    float turb = turbulent(pos3D);

    // Combine noise and turbulence with time-based color shifts
    vec3 color = hsl2rgb(vec3(
        sin(iTime + uv.x * 5.0) * 0.5 + 0.5,
        sin(iTime + uv.y * 5.0) * 0.5 + 0.5,
        baseNoise * 0.5 + turb * 0.5 + 0.5
    ));

    // Add positional gradient effect
    vec3 gradient = vec3(
        sin(uv.x * pi + iTime),
        cos(uv.y * pi + iTime * 0.5),
        sin((uv.x + uv.y) * pi + iTime)
    );

    color *= gradient;

    return vec4(color, 1.0);
}
