vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float theta = atan2(uv.y, uv.x);
    float r = length(uv * 1.5); // Scale radius

    // Add time-based rotation
    theta += iTime * 0.2;

    // Create hexagonal pattern using sine waves
    vec3 hexPattern = sin(vec3(theta + 60.0, theta, theta - 60.0) * pi / 180.0);
    hexPattern = abs(hexPattern);
    hexPattern = min(hexPattern, 1.0 - hexPattern);

    // Create pulsating RGB colors
    vec3 colorPulse = sin(iTime + vec3(0.0, 2.0, 4.0) * pi / 3.0) * 0.5 + 0.5;

    // Add noise texture
    float noiseValue = noise(uv * 10.0 + iTime);

    // Combine patterns and colors
    vec3 finalColor = mix(colorPulse, vec3(1.0), noiseValue) * hexPattern.x;

    return vec4(finalColor, 1.0);
}
