vec4 Effect2(vec2 uv) {
    // Create shifting mosaic pattern using noise and time
    vec2 st = uv * 10.0 + iTime;
    vec3 color = vec3(0.0);

    // Add chromatic aberration by offsetting UVs for each channel
    vec2 offsets[3] = vec2[3](
        vec2(-0.05, -0.05),
        vec2(0.0, 0.0),
        vec2(0.05, 0.05)
    );

    // Create mosaic pattern with noise
    for(int i = 0; i < 3; i++) {
        vec2 offsetUV = fract(st + offsets[i]);
        float n = turbulent(offsetUV * 10.0);
        color[i] = n;
    }

    // Add chromatic aberration effect based on distance from center
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center) * 2.0;
    color = mix(color, vec3(1.0), smoothstep(0.0, 1.0, dist));

    // Add some turbulence to the pattern
    float t = turbulent(vec3(st.x + iTime, st.y, 0.0)) * 0.5 + 0.5;
    color *= t;

    return vec4(color, 1.0);
}
