vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create layered sine patterns with different frequencies and amplitudes
    float s1 = sin(a * 3.0 + iTime) * 0.5;
    float s2 = sin(a * 5.0 - iTime * 2.0) * 0.4;
    float s3 = sin(r * 8.0 + a * 2.0) * 0.3;

    // Combine sine layers and add turbulence
    float pattern = (s1 + s2 + s3) * 0.5 + turbulent(vec2(a, r)) * 0.3;

    // Create color cycling effect using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + a / (2.0 * PI)), // Hue cycling over time and angle
        1.0 - abs(pattern) * 0.5,            // Saturation based on pattern
        0.7 + pattern * 0.3                 // Lightness modulation
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
