vec4 Effect2(vec2 uv) {
    // Create rotating pattern using sine functions
    float time = iTime * 0.5;
    vec2 center = vec2(0.5);
    vec2 st = uv - center;

    // Polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x) + sin(time) * 2.0;

    // Create rotating sectors with turbulence
    a += turbulent(vec3(uv * 10.0, time)) * 0.5;
    a = mod(a, PI/4.0); // Create 8 sectors

    // Color transformation using HSL shifts
    vec3 hslColor = vec3(
        sin(r * 20.0 + time) * 0.5 + 0.5,
        sin(r * 10.0 + a * 2.0) * 0.5 + 0.5,
        cos(r * 5.0 + time) * 0.5 + 0.5
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
