vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create pulsating effect using time and radius
    float pulse = sin(iTime * 3.0 + r * 5.0) * 0.5 + 0.5;

    // Create color transitions based on angle and time
    vec3 color = hsl2rgb(vec3(
        theta * 0.5 + iTime * 0.1,   // Hue varies with angle and time
        0.7,                         // Saturation
        pulse                        // Lightness varies with pulsing
    ));
    // Create radial gradient effect
    float gradient = sin(r * 8.0 + iTime) * 0.5 + 0.5;
    color *= gradient;
    return vec4(color, 1.0);
}
