//prompt: something vibrant and organic, but twisted and geometric with veins
vec4 Effect3(vec3 pos){
    // Transform position with time-based rotations
    mat3 rot = mat3(
        cos(iTime), -sin(iTime), 0,
        sin(iTime), cos(iTime), 0,
        0, 0, 1
    );

    vec3 p = pos * 2.0;
    p = (rot * p) * 2.0; // Apply rotation

    // Create cellular pattern with veins
    vec3 cell = abs(fract(p * 0.5 + iTime * 0.2) - 0.5);
    float vein = length(cell);

    // Add turbulence and organic feel
    p.x += sin(p.z * 4.0 + iTime) * 0.2;
    p.y += cos(p.x * 3.0 + iTime) * 0.1;
    p.z *= abs(sin(iTime * 0.5)) * 0.8 + 0.2;

    // Generate noise-based colors
    vec3 hue = fract(p * 0.2 + iTime);
    float n1 = turbulent(p * 4.0);
    float n2 = turbulent(p * 2.0);

    // Create vibrant color transitions with geometric veins
    vec3 color = hsl2rgb(vec3(
        (n1 + p.x * 0.5) * 0.6,
        (abs(sin(vein * 10.0)) * 0.7 + 0.3),
        0.8 - abs(p.y) * 0.4
    ));

    return vec4(color, 1.0);
}
