vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 centered = uv - 0.5;

    // Create a distance field from center
    float dist = length(centered);

    // Add some time-based movement
    float time = iTime * 0.5;

    // Create a basic sine wave pattern
    float baseSine = sin(dist * 4.0 + time) * 0.5 + 0.5;

    // Add another layer of sine waves with rotation
    vec2 rotated = centered * mat2(cos(time), -sin(time), sin(time), cos(time));
    float rotatedDist = length(rotated);
    float rotatedSine = sin(rotatedDist * 8.0 + time) * 0.5 + 0.5;

    // Combine the patterns
    float combined = (baseSine + rotatedSine) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(time * 2.0, 1.0),  // Hue
        0.7,                   // Saturation
        combined               // Lightness
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
