vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create base wave pattern with noise
    vec3 waves = sin(uv.xyy * 10.0 + time) * 0.5 + 0.5;
    waves += noise(vec3(uv, time)) * 0.4;

    // Add turbulence for more complex movement
    float turb = turbulent(vec2(uv.x * 0.5 + time, uv.y * 0.5));
    waves += sin(time + uv.y * 8.0) * 0.3;

    // Create color shifts using HSL
    vec3 color = hsl(waves.x * 0.6 + 0.3, 0.7, 0.5);
    color *= hsl2rgb(vec3(time * 0.1, 0.8, 0.9));

    // Combine effects for final output
    return vec4(color * (waves.x + waves.y) * 0.6, 1.0);
}
