vec4 Effect2(vec2 uv) {
    float time = iTime * 1.5; // Speed parameter

    // Create rotating hexagon pattern
    vec2 pos = uv * 2.0 - 1.0;
    float angle = atan(pos.y, pos.x) + time;
    float radius = length(pos);

    // Add turbulence for expanding rings effect
    float ringEffect = turbulent(vec3(pos * 5.0, time)) * 0.5;
    float hexPattern = sin(6.0 * (angle - time)) * 0.5 + 0.5;

    // Combine patterns and add color transitions
    float combined = min(hexPattern, ringEffect);
    vec3 color = hsl2rgb(vec3(fract(time * 0.1), 1.0, 0.8));

    // Add noise to colors for dynamic effect
    color += noise(vec3(pos * 4.0, time)) * 0.2;

    return vec4(combined * color + (1.0 - combined) * 0.2, 1.0);
}
