vec4 Effect2(vec2 uv) {
    // Create rotating coordinate system
    float t = iTime * 0.5;
    vec2 rotUv = uv * mat2(cos(t), sin(t), -sin(t), cos(t));

    // Flowing sine waves with dynamic frequency and amplitude
    float wave1 = sin(rotUv.x * 4.0 + t) * 0.5 + 0.5;
    float wave2 = sin(rotUv.y * 4.0 + t * 0.7) * 0.5 + 0.5;

    // Combine waves with turbulence
    vec3 noisePattern = vec3(
        noise(vec3(uv * 10.0 + t, 0.0)) * 0.5 + 0.5,
        noise(vec3(uv * 12.0 + t * 0.6, 0.0)) * 0.5 + 0.5,
        noise(vec3(uv * 8.0 + t * 0.4, 0.0)) * 0.5 + 0.5
    );

    // Dynamic color shifts using HSL
    vec3 hslColor = vec3(
        mod(t * 0.1 + turbulent(uv * 2.0), 1.0),
        0.7,
        0.8
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix patterns and colors
    float intensity = (wave1 + wave2) * 0.5;
    vec3 finalColor = mix(noisePattern, rgbColor, intensity);

    return vec4(finalColor, 1.0);
}
