//prompt: hidden complexity
vec4 Effect2(vec2 uv) {
    // Create a base pattern with hidden complexity
    vec2 distortedUV = uv + 0.1 * sin(uv.x * 8.0 + iTime) + 0.1 * cos(uv.y * 7.0 + iTime);

    // Layered patterns
    float pattern1 = abs(sin(distortedUV.x * 4.0)) + abs(cos(distortedUV.y * 4.0));
    float pattern2 = abs(sin(iTime * 0.5 + distortedUV.y * 6.0) ) * 
                    abs(cos(iTime * 0.3 + distortedUV.x * 6.0));

    // Combine patterns with color
    vec3 baseColor = vec3(0.1, 0.1, 0.2); // Dark base color
    vec3 accentColor = vec3(0.3, 0.4, 0.5);

    float blendFactor = smoothstep(0.0, 1.0, pattern1 * 0.7 + pattern2 * 0.3);
    vec3 finalColor = mix(baseColor, accentColor, blendFactor);

    // Add subtle radial pattern
    vec2 polarUV = uv * 1.5 - 0.5;
    float radius = length(polarUV) * 4.0 + iTime * 0.2;
    float rings = abs(sin(radius)) * 0.3;

    finalColor += rings * accentColor * 0.5;

    return vec4(finalColor, 1.0);
}
