vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x);
    // Add time-based rotation
    float speed = 1.0; // Adjust this value to change rotation speed
    angle += speed * iTime;
    // Create smooth color transitions using HSL
    vec3 hslColor;
    hslColor.x = mod(angle / (2.0 * PI), 1.0); // Hue based on angle
    hslColor.y = 0.7; // Saturation
    hslColor.z = 0.5; // Lightness
    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
