vec4 Effect2(vec2 uv) {
    // Create a centered coordinate system
    vec2 st = uv * 2.0 - 1.0;

    // Add time-based movement
    float time = iTime * 0.5;

    // Create two interweaving sine waves with different frequencies and phases
    float wave1 = sin(st.x * 4.0 + time) * cos(st.y * 3.0 + time);
    float wave2 = sin(st.y * 4.0 - time) * cos(st.x * 3.0 - time);

    // Combine the waves and add turbulence
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(vec2(st.x * 2.0, st.y * 2.0)) * 0.3;

    // Convert to HSL color with dynamic shifts
    vec3 color = hsl(
        mod(combined * 180.0 + time * 50.0, 360.0),  // Hue
        0.7,                                         // Saturation
        0.5                                          // Lightness
    );

    return vec4(hsl2rgb(color), 1.0);
}
