vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time-based rotation

    // Create flower-like pattern with multiple petals
    float petal = sin(a * 8.0 + iTime * 2.0); 
    float d = abs(r - 0.5 * (1.0 + 0.3 * petal));

    // Add turbulence for dynamic noise effect
    vec3 noisePos = vec3(st.x, st.y, iTime);
    float turb = turbulent(noisePos) * 0.2;

    // Combine shape and noise
    float pattern = smoothstep(0.1, 0.2, d + turb);

    // Create color with time-based shifts
    vec3 hslColor = vec3(
        fract(iTime * 0.5 + a * 0.2), // Hue variation over time and position
        0.7 + pattern * 0.3,          // Saturation
        0.5 + turb * 0.2             // Lightness influenced by noise
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
