vec4 Effect2(vec2 uv) {
    // Create base pattern using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add noise and turbulence
    float noiseVal = turbulent(vec3(uv * 4.0, iTime));
    vec3 color = hsl(theta * 0.1, r * 0.6 + 0.4, r * 0.8 + 0.2);

    // Mix in noise and add glow effect
    color *= mix(vec3(0.5), vec3(1.0), smoothstep(0.7, 1.0, r));
    color += noiseVal * 0.4;

    return vec4(color, 1.0);
}
