vec4 Effect2(vec2 uv) {
    // Create a checkerboard pattern with dynamic movement
    vec2 st = uv * 10.0; // Scale up for more cells
    vec2 ipos = floor(st); // Get integer position

    // Add time-based movement to the pattern
    float timeScale = sin(iTime * 0.5) * 0.5 + 0.5;
    st += timeScale * 0.3; 

    // Checkerboard condition using modulo operation
    float check = mod(ipos.x + ipos.y, 2.0);

    // Create smooth color transitions using sine functions
    vec3 color1 = hsl(0.6, 0.8, 0.5); // Blue base color
    vec3 color2 = hsl(0.1, 0.8, 0.5); // Red base color

    // Smooth transition between colors using sine wave
    float mixFactor = sin(iTime * 0.4 + uv.x * 2.0) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixFactor);

    // Apply checkerboard pattern
    finalColor *= check == 0.0 ? 1.0 : 0.7; // Alternate brightness

    return vec4(finalColor, 1.0);
}
