vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create hexagonal pattern using angle and radius
    a += iTime; // Rotate over time
    float angle = mod(a + sin(r * 5.0 + iTime) * 0.5, PI * 2.0);
    float segment = mod(angle / (PI/3.0), 1.0); // Divide into hex segments

    // Create pulsating color effect
    vec3 color;
    color.x = sin(iTime + r * 5.0) * 0.5 + 0.5; // Red component
    color.y = sin(iTime + r * 5.0 + PI/2.0) * 0.5 + 0.5; // Green component
    color.z = sin(iTime + r * 5.0 + PI) * 0.5 + 0.5; // Blue component

    // Add noise to create organic movement
    float noiseVal = noise(vec2(r, a)) * 0.1;
    segment += noiseVal;

    // Create hexagonal boundaries with pulsing effect
    float border = smoothstep(0.4 + sin(iTime) * 0.3, 0.5 + sin(iTime) * 0.3, segment);

    // Mix colors for final output
    vec3 finalColor = mix(vec3(1.0), color, border);

    return vec4(finalColor, 1.0);
}
