vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Animate the pattern over time
    theta += iTime * 0.5;
    r += iTime * 0.5;

    // Create a grid of circles using polar coordinates
    vec2 st = vec2(theta, r);
    st.x *= 1.0 / (pi * 2.0); // Normalize angle to [0,1]
    st.y *= 0.5; // Scale radius

    // Add some turbulence for variation
    float turb = turbulent(uv * 3.0 + iTime) * 0.5;

    // Create color based on distance from center and time
    vec3 color = hsl(theta * 2.0, 1.0, 0.5 + 0.5 * sin(r * 10.0 + iTime));

    // Mix in some turbulence for depth
    color *= 1.0 + turb * 0.5;

    return vec4(color, 1.0 - length(uv) * 0.5);
}
