vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form with rotation
    vec2 polar = vec2(
        atan2(uv.y - 0.5, uv.x - 0.5) + iTime * 0.5,
        length(uv - 0.5)
    );

    // Create hexagonal grid pattern
    vec2 grid = floor(polar * 10.0 + vec2(0.5, 0.5));
    float n = noise(grid);

    // Add noise to create organic shape variations
    polar += vec2(n * 0.1, n * 0.3);

    // Create hexagonal pattern with inner details
    float hex = sin(6.0 * polar.x) + cos(6.0 * polar.x);
    float pattern = abs(hex) < 0.5 ? 1.0 : 0.0;
    float inner = sin(12.0 * polar.x) < 0.5 ? 1.0 : 0.0;

    // Combine patterns and add glow effect
    pattern *= inner;
    float glow = smoothstep(0.4, 0.5, polar.y);

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        0.5 + sin(iTime * 0.1 + grid.x) * 0.5,
        0.7,
        0.8
    ));

    return vec4(color * (pattern + glow * 0.5), 1.0);
}
