vec4 Effect2(vec2 uv) {
    // Create flowing patterns using cosine waves
    vec2 st = uv * 5.0 + vec2(cos(iTime * 0.5), sin(iTime * 0.5));

    // Add turbulence for dynamic effect
    float turb = turbulent(st * 0.5 + iTime);

    // Create color shifts using HSL
    vec3 color = hsl(
        mod(iTime * 0.1 + turb, 1.0),  // Hue variation over time and space
        0.7,                            // Saturation
        0.5 + turb * 0.2               // Lightness modulation
    );

    return vec4(color, 1.0);
}
