vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates for rotational symmetry
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create rotating sine wave pattern with multiple frequencies
    float val = sin(a * 4.0 + iTime * 2.0) * 0.5 +
                sin(a * 8.0 + iTime * 3.0) * 0.3 +
                sin(a * 16.0 + iTime * 4.0) * 0.2;

    // Add turbulence for dynamic noise effect
    val += turbulent(vec2(r, a)) * 0.1;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(val * 0.5 + 0.5, 0.8, 0.6));

    // Scale the color by the pattern value for brightness variation
    color *= val * 0.5 + 0.5;

    return vec4(color, 1.0);
}
