vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create rotating sine wave pattern
    float speed = 0.5;
    float time = iTime * speed;
    float rotation = sin(theta + time) * 0.5 + 0.5;

    // Combine radius and rotation for color variation
    vec3 color = vec3(
        sin(r * 10.0 + time) * 0.5 + 0.5,
        sin(r * 8.0 + time + PI/2.0) * 0.5 + 0.5,
        sin(r * 6.0 + time + PI) * 0.5 + 0.5
    );

    // Add noise for texture
    vec2 noiseUV = uv * 10.0;
    float n = noise(noiseUV);
    color += n * 0.3;

    // Convert to HSL and back to RGB for rotating colors
    vec3 hslColor = vec3(
        rotation,
        sin(time) * 0.5 + 0.5,
        cos(time) * 0.5 + 0.5
    );
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
