vec4 Effect2(vec2 uv) {
    // Create flowing sine wave pattern
    float time = iTime * 0.5;
    float wave1 = sin(uv.y * 10.0 + time) * 0.5 + 0.5;
    float wave2 = sin(uv.x * 10.0 - time) * 0.5 + 0.5;

    // Create rotating color gradient
    vec2 polar = uv * 2.0 - 1.0;
    float angle = atan2(polar.y, polar.x) + time;
    float radius = length(polar);

    // Use HSL to RGB conversion for smooth color transitions
    vec3 baseColor = hsl(angle * 0.5 + 0.5, 0.7, 0.6);
    vec3 secondaryColor = hsl(angle * 0.5 + 0.2, 0.7, 0.4);

    // Add turbulence for dynamic effect
    float turb = turbulent(uv * 10.0 + time) * 0.5 + 0.5;

    // Combine patterns and colors
    vec3 color = mix(baseColor, secondaryColor, wave1 * wave2 * radius * 0.5);
    color *= pow(wave1 * wave2 * turb, 0.5);

    return vec4(color, 1.0);
}
