//prompt:a fractal
vec4 Effect2(vec2 uv) {
    // Mandelbrot fractal
    vec2 c = (uv * 2.0 - 1.0) * vec2(1.5, 1.0);  // Scale and translate coordinates

    vec2 z = vec2(0.0);
    float maxIterations = 100.0;
    float iterations = 0.0;

    while (iterations < maxIterations && length(z) <= 2.0) {
        vec2 nextZ = vec2(
            z.x * z.x - z.y * z.y + c.x,
            2.0 * z.x * z.y + c.y
        );
        z = nextZ;
        iterations++;
    }

    // Color based on iteration count
    float colorValue = min(iterations / maxIterations, 1.0);
    vec3 color = mix(vec3(0.4, 0.7, 0.4), vec3(0.9, 0.2, 0.2), colorValue);

    return vec4(color, 1.0);
}
