vec4 Effect2(vec2 uv) {
    // Create a base pattern using UV coordinates and time
    vec2 st = uv * 10.0 + sin(iTime) * 5.0;

    // Add noise to create organic patterns
    float n1 = noise(st);
    float n2 = turbulent(st + iTime);

    // Combine noise layers with geometric patterns
    float pattern = (n1 + n2) * 0.5;
    pattern += sin(st.x * 0.5 + iTime) * 0.3;
    pattern += cos(st.y * 0.5 - iTime) * 0.3;

    // Create pulsating shapes using geometric functions
    float distance = length(uv);
    float angle = atan2(uv.y, uv.x);
    float shape = sin(distance * 10.0 + iTime) * cos(angle * 4.0);

    // Combine pattern and shape with color transitions
    vec3 color = hsl(
        (pattern + shape) * 0.5 + 0.5,
        0.7 + abs(sin(iTime)) * 0.3,
        0.5 + cos(iTime) * 0.2
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
