vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation
    angle += iTime * 5.0;

    // Create kaleidoscopic effect by repeating angles
    float sector = mod(angle * 3.0, PI * 2.0);

    // Generate triangle pattern using sine waves
    vec2 triPos = vec2(sin(sector + iTime), cos(sector - iTime));
    float triDistance = length(triPos) * radius;

    // Create flowing gradient background
    vec3 bgColor = hsl2rgb(vec3(mod(angle * 0.5 + iTime, 1.0), 0.7, 0.8));

    // Add turbulence to create dynamic patterns
    float turb = turbulent(vec3(uv * 0.5, iTime)) * 0.5;
    bgColor += vec3(turb);

    // Create triangle color pattern
    vec3 triColor = hsl2rgb(vec3(mod(angle * 0.3 + iTime, 1.0), 0.8, 0.9));

    // Combine background and triangles with mix based on distance
    vec3 finalColor = mix(bgColor, triColor, step(0.5, radius) * (1.0 - turb));

    return vec4(finalColor, 1.0);
}
