vec4 Effect2(vec2 uv) {
    // Create base noise pattern with time offset
    vec3 noise1 = vec3(noise(uv + iTime), noise(uv * 2.0 - iTime), noise(uv * 0.5));

    // Add turbulent movement
    float turb = turbulent(vec3(uv, iTime * 0.1)) * 0.5 + 0.5;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1) * 360.0,
        0.7,
        turb * 0.8 + 0.2
    ));

    // Add depth and layers using multiple noise scales
    float detail = (noise(uv * 4.0 + iTime) * 0.5 + 0.5) +
                  (noise(uv * 2.0 - iTime) * 0.3);

    // Combine all elements for final color
    vec3 finalColor = mix(color, vec3(1.0), detail * 0.6) *
                     (turb * 1.5);

    return vec4(finalColor, 1.0);
}
