vec4 Effect2(vec2 uv) {
    // Create dynamic sine wave patterns with time-based animation
    float time = iTime * 0.5;

    // Wave 1: Horizontal interweaving pattern
    vec2 st1 = uv + vec2(sin(uv.y * 3.0 + time) * 0.2, cos(uv.x * 3.0 + time + PI) * 0.2);
    float wave1 = sin(st1.x * 4.0 + time) * 0.5 + 0.5;

    // Wave 2: Vertical interweaving pattern
    vec2 st2 = uv + vec2(cos(uv.y * 3.0 + time + PI/2.0) * 0.2, sin(uv.x * 3.0 + time + PI/2.0) * 0.2);
    float wave2 = sin(st2.y * 4.0 + time + PI/2.0) * 0.5 + 0.5;

    // Combine waves with complementary colors
    vec3 color1 = hsl2rgb(vec3(0.5, 0.7, wave1));
    vec3 color2 = hsl2rgb(vec3(0.0, 0.7, wave2));

    // Mix colors based on wave patterns and add noise for texture
    vec3 finalColor = mix(color1, color2, wave2);
    finalColor += noise(uv * 4.0 + time) * 0.2;

    return vec4(finalColor, 1.0);
}
