vec4 Effect2(vec2 uv) {
    vec3 col = vec3(0.0);
    float time = iTime * 0.5;

    // Generate multiple layers of lines
    for(float i = 0.0; i < 3.0; i++) {
        // Calculate line position and rotation
        float angle = sin(time + i) * 0.5 + i * 0.2;
        vec2 pos = vec2(sin(time + i * 0.7) * 0.5, cos(time + i * 0.3) * 0.5);

        // Create line pattern
        float d = abs(length(uv - pos));
        float w = 0.1 + sin(time + i) * 0.05;
        float glow = smoothstep(w, 0.0, d);

        // Add noise and turbulence
        vec3 noisePos = vec3(uv * 2.0 + time, time);
        float turb = turbulent(noisePos) * 0.1;
        glow *= 1.0 + turb;

        // Color transition
        vec3 hslCol = vec3(120.0 + i * 60.0, 1.0, 0.5 + glow * 0.5);
        col += hsl2rgb(hslCol) * glow;
    }

    return vec4(col, 1.0);
}
