vec4 Effect2(vec2 uv) {
    // Create flowing velocity based on time and UV coordinates
    vec2 st = uv * 5.0;
    float time = iTime * 0.5;

    // Add some noise to create natural-looking flow
    vec2 velocity = vec2(
        sin(st.x + time) * 0.5 + noise(vec3(uv, time)) * 0.1,
        cos(st.y + time) * 0.5 + turbulent(vec2(uv)) * 0.2
    );

    // Create color transitions using HSL
    vec3 color = hsl(
        mod(time * 0.1 + uv.x * 2.0, 1.0),  // Hue variation over time and space
        0.7,                                  // Saturation
        0.5                                   // Lightness
    );

    // Add some noise to the color for more dynamic effect
    float detail = turbulent(vec3(uv * 2.0 + velocity, time)) * 0.5;
    color = hsl2rgb(vec3(
        mod(color.r + detail * 0.1, 1.0),
        color.g,
        color.b
    ));

    // Combine everything with some alpha for transparency effect
    return vec4(color, 1.0 - length(velocity) * 0.5);
}
