vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Convert to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x);

    // Create multiple layers of rotating shapes
    float shape1 = sin(time * 0.5 + angle * 3.0) * 0.5 + 0.5;
    float shape2 = sin(time * 0.7 + angle * 5.0) * 0.3 + 0.3;

    // Add noise for texture
    float noiseValue = noise(vec2(uv.x * 10.0, uv.y * 10.0) + time);
    float turbulence = turbulent(vec2(uv.x * 5.0, uv.y * 5.0));

    // Combine shapes and noise
    float pattern = shape1 * shape2 * (1.0 - noiseValue * 0.5);
    pattern += turbulence * 0.3;

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3(0.2 + time * 0.1, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.6 + time * 0.15, 1.0, 0.7));

    // Mix colors based on pattern
    vec3 finalColor = mix(color1, color2, pattern);

    return vec4(finalColor, 1.0);
}
