vec4 Effect2(vec2 uv) {
    // Create pulsating radial pattern using noise and time modulation
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);

    // Polar coordinates for radial effect
    vec2 polar = vec2(length(st), atan2(st.y, st.x));
    polar.y *= 0.5; // Reduce angle frequency

    // Time-based pulsation
    float timePulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Create noise pattern with frequency modulation
    vec2 noiseScale = vec2(10.0, 10.0);
    float noiseValue = turbulent(polar * noiseScale + timePulse * 10.0);

    // Combine radial gradient and noise for color variation
    float lightness = radius * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(0.6, 0.8, lightness));
    color *= mix(1.0, 0.5, abs(noiseValue) * timePulse);

    // Add pulsating glow effect
    float glow = smoothstep(0.7, 1.0, radius + sin(iTime) * 0.2);
    color += vec3(glow * 0.4);

    return vec4(color, 1.0);
}
