vec4 Effect2(vec2 uv) {
    // Create base color with dynamic hue shift
    vec3 color = hsl(
        sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5,  // Hue
        0.7,  // Saturation
        0.5   // Lightness
    );

    // Add movement and noise to create plasma effect
    vec2 st = uv * 4.0;
    float timeOffset = iTime * 0.1;

    // Create moving pattern using noise
    float noise1 = noise(vec3(st + timeOffset, 0.0)) * 0.5;
    float noise2 = noise(vec3(st * 0.5 + timeOffset * 0.7, 0.0)) * 0.3;

    // Combine noise layers
    float combinedNoise = noise1 + noise2;

    // Add turbulence for light ripples
    vec2 rippleUV = uv * 8.0 + vec2(sin(iTime) * 2.0, cos(iTime) * 2.0);
    float ripple = turbulent(rippleUV) * 0.4;

    // Combine all elements
    color *= combinedNoise + 0.5;  // Modulate base color with noise
    color += ripple;               // Add light ripples

    return vec4(hsl2rgb(color), 1.0);
}
