vec4 Effect2(vec2 uv) {
    vec3 pos = vec3(uv * 2.0 - 1.0, iTime * 0.5);
    float t = iTime * 0.2;

    // Complex noise pattern with turbulence
    float n = turbulent(pos + vec3(t)) * 0.5 + 0.5;
    n += noise(vec3(uv * 4.0, t)) * 0.3;

    // Color based on position and noise
    vec3 color = hsl2rgb(vec3(
        (n * 0.6 + 0.3) * pi,
        1.0 - abs(n - 0.5) * 2.0,
        n * 0.3 + 0.2
    ));

    return vec4(color, 1.0);
}
