vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    float dist = length(uv);

    // Add pulsation based on time
    float pulse = sin(iTime * 0.5) / 3.0;
    dist += pulse;

    // Create color transition from red to blue
    vec3 colorHSL = vec3(
        (dist + sin(iTime) / 4.0) * 0.5,  // Hue: transitions between red and blue
        0.8,                              // Saturation
        mix(0.5, 0.7, dist)              // Lightness varies with distance
    );

    // Add noise for extra texture
    colorHSL += vec3(noise(uv * 10.0 + iTime), noise(uv * 10.0 + iTime + 1.0), noise(uv * 10.0 + iTime + 2.0)) * 0.1;

    return vec4(hsl2rgb(colorHSL), 1.0);
}
